<form id="form"><input type="text" name="fname" id="answer" placeholder="Antwort..."><input type="button" value="senden" onClick="sendAnswer()"></form><script>$(document).ready(function () { var from = settings["from"];  var till = settings["till"];  var mid = settings["from"]/2; var type = settings["type"];  if(settings["type"] == "Text")  {   $("#form").html('<input type="text" name="fname" id="answer" placeholder="Antwort..."><input type="button" value="senden" onClick="sendAnswer()">')  }   else if(settings["type"] == "Zahl") {   $("#form").html('<label for="slider">Zahl</label><input type="range" name="slider" id="answer" value="'+mid+'" min="'+from+'" max="'+till+'" /><input type="button" value="senden" onClick="sendAnswer()">') } else if(settings["type"] == "Datum")  {   $("#form").html('<label for="date">Datum</label><input type="date" id="answer" data-role="date" data-inline="true"><input type="button" value="senden" onClick="sendAnswer()">') } });
